package org.openecard.common.util

import dev.mokkery.answering.returns
import dev.mokkery.every
import dev.mokkery.mock
import org.openecard.bouncycastle.tls.Certificate
import org.openecard.bouncycastle.tls.TlsServerCertificate
import org.openecard.bouncycastle.tls.crypto.TlsCertificate
import org.openecard.common.util.StringUtils.toByteArray
import org.openecard.common.util.TR03112Utils.checkSameOriginPolicy
import org.openecard.common.util.TR03112Utils.isInCommCertificates
import org.openecard.common.util.TR03112Utils.isRedirectStatusCode
import org.testng.Assert
import org.testng.annotations.Test
import java.io.IOException
import java.net.MalformedURLException
import java.net.URL

/**
 *
 * @author Dirk Petrautzki
 */
class TR03112UtilsTest {
	@Test
	@Throws(MalformedURLException::class)
	fun testSameOriginPolicy() {
		val url1 = URL("http://example.com/dir/page.html")
		var url2 = URL("http://example.com/dir/inner/another.html")
		var result = checkSameOriginPolicy(url1, url2)
		Assert.assertTrue(result)
		url2 = URL("http://example.com/dir2/another.html")
		result = checkSameOriginPolicy(url1, url2)
		Assert.assertTrue(result)
		url2 = URL("http://www.example.com/dir/another.html")
		result = checkSameOriginPolicy(url1, url2)
		Assert.assertFalse(result)
		url2 = URL("http://example.com:89/dir/another.html")
		result = checkSameOriginPolicy(url1, url2)
		Assert.assertFalse(result)
		url2 = URL("https://example.com/dir/another.html")
		result = checkSameOriginPolicy(url1, url2)
		Assert.assertFalse(result)
	}

	@Test
	@Throws(IOException::class)
	fun testInCommCertificates() {
		val mockTlsCert2 =
			mock<TlsCertificate> {
				every { encoded } returns x509Certificate
			}
		val mockCert =
			mock<Certificate> {
				every { getCertificateAt(0) } returns mockTlsCert2
			}
		val mockTlsCert =
			mock<TlsServerCertificate> {
				every { certificate } returns mockCert
			}

		val listOfHashes = ArrayList<ByteArray?>()
		listOfHashes.add(correctHash)
		var result = isInCommCertificates(mockTlsCert, listOfHashes, null)
		Assert.assertTrue(result)
		listOfHashes.clear()
		listOfHashes.add(incorrectHash)
		result = isInCommCertificates(mockTlsCert, listOfHashes, null)
		Assert.assertFalse(result)
	}

	@Test
	fun testIsRedirectStatusCode() {
		var result = isRedirectStatusCode(200)
		Assert.assertFalse(result)
		result = isRedirectStatusCode(300)
		Assert.assertFalse(result)
		result = isRedirectStatusCode(301)
		Assert.assertTrue(result)
		result = isRedirectStatusCode(302)
		Assert.assertTrue(result)
		result = isRedirectStatusCode(303)
		Assert.assertTrue(result)
		result = isRedirectStatusCode(304)
		Assert.assertFalse(result)
		result = isRedirectStatusCode(305)
		Assert.assertFalse(result)
		result = isRedirectStatusCode(306)
		Assert.assertFalse(result)
		result = isRedirectStatusCode(307)
		Assert.assertTrue(result)
		result = isRedirectStatusCode(400)
		Assert.assertFalse(result)
	}

	companion object {
		private val incorrectHash = toByteArray("898F265C130D567741FA5F695749C691289128AD398D3D43C476BC7C2C6AF123")
		private val correctHash = toByteArray("898F265C130D567741FA5F695749C691289128AD398D3D43C476BC7C2C6AFFF3")
		private val x509Certificate =
			toByteArray(
				"",
			)
	}
}
