package org.openecard.crypto.common.asn1.eac

import org.openecard.bouncycastle.jce.ECNamedCurveTable
import org.openecard.bouncycastle.jce.spec.ElGamalParameterSpec
import java.math.BigInteger
import java.security.spec.AlgorithmParameterSpec

/**
 * See RFC 5114, http://tools.ietf.org/html/rfc5114
 *
 * @author Moritz Horsch
 */
class StandardizedDomainParameters(
	index: Int,
) : DomainParameters(getDomainParameter(index)) {
	companion object {
		private val curves: Map<Int, Any>

		/**
		 * Instantiates a new standardized domain parameters.
		 *
		 * @param index the index of the standardized domain parameters
		 */
		private fun getDomainParameter(index: Int): AlgorithmParameterSpec {
			val value: Any = requireNotNull(curves[index]) { "Wrong index for standardized domain parameter" }

			return if (index >= 0 && index <= 2) {
				value as AlgorithmParameterSpec
			} else if (index >= 8 && index <= 18) {
				ECNamedCurveTable.getParameterSpec(value.toString())
			} else {
				throw IllegalArgumentException("Wrong index for standardized domain parameter")
			}
		}

		// See RFC 5114, Section 2.1. 1024-bit MODP Group with 160-bit Prime Order Subgroup
		init {
			val gfp_1024_160 =
				ElGamalParameterSpec(
					BigInteger(
						"B10B8F96A080E01DDE92DE5EAE5D54EC52C99FBCFB06A3C69A6A9DCA52D23B616073E28675A23D189838EF1E2EE652C013ECB4AEA906112324975C3CD49B83BFACCBDD7D90C4BD7098488E9C219A73724EFFD6FAE5644738FAA31A4FF55BCCC0A151AF5F0DC8B4BD45BF37DF365C1A65E68CFDA76D4DA708DF1FB2BC2E4A4371",
						16,
					),
					BigInteger(
						"A4D1CBD5C3FD34126765A442EFB99905F8104DD258AC507FD6406CFF14266D31266FEA1E5C41564B777E690F5504F213160217B4B01B886A5E91547F9E2749F4D7FBD7D3B9A92EE1909D0D2263F80A76A6A24C087A091F531DBF0A0169B6A28AD662A4D18E73AFA32D779D5918D08BC8858F4DCEF97C2A24855E6EEB22B3B2E5",
						16,
					),
				)

			// See RFC 5114, Section 2.1. 2048-bit MODP Group with 224-bit Prime Order Subgroup
			val gfp_2048_224 =
				ElGamalParameterSpec(
					BigInteger(
						"",
						16,
					),
					BigInteger(
						"",
						16,
					),
				)

			// See RFC 5114, Section 2.1. 2048-bit MODP Group with 256-bit Prime Order Subgroup
			val gfp_2048_256 =
				ElGamalParameterSpec(
					BigInteger(
						"",
						16,
					),
					BigInteger(
						"",
						16,
					),
				)

			// See BSI-TR-03110 version 2.05 section A.2.1.1.
			curves =
				mapOf(
					0 to gfp_1024_160,
					1 to gfp_2048_224,
					2 to gfp_2048_256,
//        map.put(0, "");
//        map.put(1, "");
//        map.put(2, "");
					// 3 - 7 RFU
					8 to "secp192r1",
					9 to "BrainpoolP192r1",
					10 to "secp224r1",
					11 to "BrainpoolP224r1",
					12 to "secp256r1",
					13 to "BrainpoolP256r1",
					14 to "BrainpoolP320r1",
					15 to "secp384r1",
					16 to "BrainpoolP384r1",
					17 to "BrainpoolP512r1",
					18 to "secp521r1",
					// 19 - 31 RFU
				)
		}
	}
}
