package org.openecard.crypto.common.asn1.cvc

import io.github.oshai.kotlinlogging.KotlinLogging
import org.openecard.common.util.ByteUtils
import org.openecard.common.util.StringUtils
import org.openecard.crypto.common.asn1.eac.EFCardAccessTest
import org.slf4j.Logger
import org.slf4j.LoggerFactory
import org.testng.Assert
import org.testng.annotations.Test
import java.io.ByteArrayOutputStream
import java.text.DateFormat
import java.text.SimpleDateFormat

/**
 *
 * @author Moritz Horsch
 */
class CardVerifiableCertificateViewer {
	private lateinit var certs: List<CardVerifiableCertificate>

	fun init() {
		certs = listOf(
			CardVerifiableCertificate(StringUtils.toByteArray("")),
			CardVerifiableCertificate(StringUtils.toByteArray("")),
			CardVerifiableCertificate(StringUtils.toByteArray("")),
			CardVerifiableCertificate(StringUtils.toByteArray("")),
			CardVerifiableCertificate(loadTestFile("cert_cvca.cvcert")!!),
			CardVerifiableCertificate(loadTestFile("cert_dv.cvcert")!!),
			CardVerifiableCertificate(loadTestFile("cert_at.cvcert")!!),
			CardVerifiableCertificate(loadTestFile("cert_at_malformed.cvcert")!!),
		)
	}

	@Test(enabled = !true)
	fun view() {
		init()

		for (c in certs) {
			val dateFormat: DateFormat = SimpleDateFormat()

			println("Certificate Profile Identifier: " + ByteUtils.toHexString(c.cPI, true))
			println("Certification Authority Reference: " + String(c.cAR.toByteArray()))
			println("Certificate Holder Reference: " + String(c.cHR.toByteArray()))
			println("Role: " + c.cHAT.role!!.name)
			println("CHAT: " + c.cHAT.toString())
			println("EffectiveDate: " + dateFormat.format(c.getEffectiveDate().getTime()))
			println("ExpirationDate: " + dateFormat.format(c.getExpirationDate().getTime()))

			if (c.extensions != null) {
				println("Extensions: ")
				println(ByteUtils.toHexString(c.extensions, true))
			}

			println("")
		}
	}

	@Throws(Exception::class)
	private fun loadTestFile(file: String): ByteArray? {
		val path = "/$file"
		val ins = EFCardAccessTest::class.java.getResourceAsStream(path)
		val baos = ByteArrayOutputStream(ins!!.available())
		try {
			var b: Int
			while ((ins.read().also { b = it }) != -1) {
				baos.write(b.toByte().toInt())
			}
		} catch (e: Exception) {
			Assert.fail(e.message)
		}
		return baos.toByteArray()
	}

}
